import React, { useState } from 'react';
import axios from 'axios';

function App() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [title, setTitle] = useState('');
    const [ingredients, setIngredients] = useState('');
    const [instructions, setInstructions] = useState('');
    const [token, setToken] = useState('');

    const register = async () => {
        await axios.post('/register', { username, password });
    };

    const login = async () => {
        const res = await axios.post('/login', { username, password });
        setToken(res.data.token);
    };

    const addRecipe = async () => {
        await axios.post('/recipes', { title, ingredients, instructions }, { headers: { Authorization: `Bearer ${token}` } });
    };

    return (
        <div>
            <h2>Register</h2>
            <input type="text" value={username} onChange={e => setUsername(e.target.value)} placeholder="Username" />
            <input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Password" />
            <button onClick={register}>Register</button>

            <h2>Login</h2>
            <input type="text" value={username} onChange={e => setUsername(e.target.value)} placeholder="Username" />
            <input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Password" />
            <button onClick={login}>Login</button>

            <h2>Add Recipe</h2>
            <input type="text" value={title} onChange={e => setTitle(e.target.value)} placeholder="Title" />
            <textarea value={ingredients} onChange={e => setIngredients(e.target.value)} placeholder="Ingredients" />
            <textarea value={instructions} onChange={e => setInstructions(e.target.value)} placeholder="Instructions" />
            <button onClick={addRecipe}>Add Recipe</button>
        </div>
    );
}

export default App;
