// src/index.js
import React from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import App from './App';
import Login from './components/Login';
import Register from './components/Register';
import axios from './axios';

// Funkcja sprawdzająca token
const isAuthenticated = () => {
    return localStorage.getItem('token') !== null;
};

// Ustawienie domyślnego nagłówka autoryzacji dla axios
if (isAuthenticated()) {
    axios.defaults.headers.common['Authorization'] = `Bearer ${localStorage.getItem('token')}`;
}

ReactDOM.render(
    <Router>
        <Routes>
            <Route path="/*" element={isAuthenticated() ? <App /> : <Navigate to="/login" />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
        </Routes>
    </Router>,
    document.getElementById('root') // Upewnij się, że element o ID "root" istnieje w index.html
);
